# Quel est le handle le plus court de cette liste ?

def smaller_handle(data) 
    value_mini = data[0] 
    data.each { | value |
    if value.length < value_mini.length 
        value_mini = value 
    end
    }
    puts "Le nom le plus court est #{value_mini}"
end

    puts smaller_handle(data)

# Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)

def handle_character(data)
    counter = 0
    data.each {|value| 
    if value.length == 6
        counter += 1
    end
}
    puts "Il y a #{counter} handle contenant 5 caractère."
end

puts handle_character(data)

# Combien commencent par une majuscule (première lettre juste après le @) ?

def handle_character(data)
    counter = 0
    data.each {|value| 
    if value[1] === value[1].upcase
        counter += 1
    end
}
puts "Il y a #{counter} handle qui commencent par une majuscule."
end

puts handle_character(data)

# Trie la liste de handle par ordre alphabétique.

def handle_character(data)
    puts "#{data.sort}"
end

puts handle_character(data)

# Trie la liste de handle par taille des handle

def handle_character(data)
    puts "#{data.sort { |a, b| a.length <=> b.length }}"
end

puts handle_character(data)

# Quelle est la position dans l'array de la personne @epenser ?

def handle_character(data)
    puts "La position dans l'array de la personne est #{data.index("@epenser")}"
end

puts handle_character(data)

# Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)

def handle_character(data)
    handle_lengths = data.map(&:length)

length_counts = handle_lengths.each_with_object(Hash.new(0)) do |length, counts|
  counts[length] += 1
end

